-- load up html template stringsdofile("lua/html.lua")local html=require("wetgenes.html")local sys=require("wetgenes.aelua.sys")local dat=require("wetgenes.aelua.data")local log=require("wetgenes.aelua.log").log -- grab the func from the packagelocal wet_string=require("wetgenes.string")local str_split=wet_string.str_splitlocal serialize=wet_string.serialize--------------------------------------------------------------------------------- the serv function, named the same as the file it is in-------------------------------------------------------------------------------function serv_chan(srv)local function put(a,b)	srv.put(html.get(a,b))end	srv.set_mimetype("text/html")	put("header",{})		put("footer",{time=math.ceil((sys.count()-srv.count_start)*1000)/1000})	end