local core=require("wetgenes.aelua.sys.core")local io=iomodule("wetgenes.aelua.sys")function time()	return core.time()endfunction clock()	return core.clock()end--------------------------------------------------------------------------------- check if the given file exists-------------------------------------------------------------------------------function file_exists(filename)--[[	local f=io.open(filename,"r")	if f then		f:close()		return true	end	return false]]	return core.file_exists(filename)end--------------------------------------------------------------------------------- find a given lua file, within the lua path-- do not pass in the .lua extension-- returns a path to a file you can then open-------------------------------------------------------------------------------function file_find_lua(name)	return "lua/"..name..".lua"end